    private void txtHODFocusLost(java.awt.event.FocusEvent evt) {//GEN-FIRST:event_txtHODFocusLost
        // TODO add your handling code here:
        if(txtHOD.getText().trim().equals("")) {
            //Check whether any other component is getting focus after validation
            if(!GUIManager.DoNotShowWarning) {
                GUIManager.DoNotShowWarning=true; //Stops the recursion 
                JOptionPane.showMessageDialog(null,"You must enter HOD Name."); //Show Validation
                GUIManager.ForcefullyFocus(this,txtDeptName); //Forcefully sets the focus.
                GUIManager.DoNotShowWarning=false; //Reset the flag 
                GUIManager.MakeFocusable(this,true); //Make all other component focusable.
            }
        }
        
    }//GEN-LAST:event_txtHODFocusLost

