    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // TODO add your handling code here:
        try
        {
           int NewRow=0;
           
           TReportWriter.SimpleDataProvider.TTable objData=new TReportWriter.SimpleDataProvider.TTable();
           
           objData.AddColumn("SR_NO");
           objData.AddColumn("NAME");
           objData.AddColumn("ADDRESS");
           

           objData.AddRow();
           NewRow++;
           
           objData.Rows(NewRow).setValue("SR_NO","1");
           objData.Rows(NewRow).setValue("NAME","Name 1");
           objData.Rows(NewRow).setValue("ADDRESS","Address one");

           
           objData.AddRow();
           NewRow++;
           
           objData.Rows(NewRow).setValue("SR_NO","2");
           objData.Rows(NewRow).setValue("NAME","Name 2");
           objData.Rows(NewRow).setValue("ADDRESS","Address two");
           
           objData.AddRow();
           NewRow++;
           
           objData.Rows(NewRow).setValue("SR_NO","3");
           objData.Rows(NewRow).setValue("NAME","Name 3");
           objData.Rows(NewRow).setValue("ADDRESS","Address three");
           
           objData.AddRow();
           NewRow++;
           
           objData.Rows(NewRow).setValue("SR_NO","4");
           objData.Rows(NewRow).setValue("NAME","Thando ");
           objData.Rows(NewRow).setValue("ADDRESS","Vaghodia Road");
           

           objData.AddRow();
           NewRow++;
           
           objData.Rows(NewRow).setValue("SR_NO","5");
           objData.Rows(NewRow).setValue("NAME","Shah ");
           objData.Rows(NewRow).setValue("ADDRESS","Swapnil Shah");
           
           TReportEngine objEngine=new TReportEngine();
           
           objEngine.PreviewReport("/tomcat/webapps/ReportWriter/customdata.rpt",new HashMap(),objData);
           
           
        }
        catch(Exception e)
        {
            
        }
    }//GEN-LAST:event_jButton2ActionPerformed

